(()=>{var e=class{constructor(t){if(this.element=document.querySelector(t),!this.element)throw new Error(`Element with selector "${t}" not found.`);this.updateTime(),this.start()}formatTime(t){let n=i=>String(i).padStart(2,"0"),o=n(t.getHours()),s=n(t.getMinutes()),r=n(t.getSeconds());return`${o}:${s}:${r}`}updateTime(){let t=new Date;this.element.textContent=this.formatTime(t)}start(){this.interval=setInterval(()=>this.updateTime(),1e3)}stop(){clearInterval(this.interval)}};var h=new e(".hero_clock");})();
